const t=JSON.parse('{"key":"v-59ea6faa","path":"/article/%E5%9F%BA%E4%BA%8E%E4%BA%8B%E4%BB%B6%E6%9C%BA%E5%88%B6%E7%9A%84%E6%9C%8D%E5%8A%A1%E5%86%85%E5%89%8A%E5%B3%B0%E5%92%8C%E4%BD%8E%E8%80%A6%E5%90%88-%E5%90%8C%E6%AD%A5-%E6%93%8D%E4%BD%9C%E7%9A%84%E6%80%9D%E8%80%83%E4%B8%8E%E5%AE%9E%E7%8E%B0.html","title":"基于事件机制的服务内削峰和低耦合(同步)操作的思考与实现","lang":"zh-CN","frontmatter":{"title":"基于事件机制的服务内削峰和低耦合(同步)操作的思考与实现","icon":"info","isOriginal":true,"date":"2021-10-27T13:57:00.000Z","tag":["Mekatok","开发笔记"],"category":["Mekatok"],"description":"问题描述 去年底在做项目的时候,我们有这样的一个需求,用户上传文档操作,首先将文档保存至服务器后再上传到ES数据库中解析文档.解析文档是一个很消耗时间的操作,我们不能等待解析完成后再对用户返回操作成功. 今年初在做项目的时候,我们有这样的一个需求,用户接收消息后,需要根据是否节假日且用户是否设置节假日自动回复对消息进行自动回复处理.且自动回复操作的成功与否要影响到消息是否接收.我们在分析需求后觉得这必须是一个事务性的服务.自动回复失败将不接收消息.","head":[["meta",{"property":"og:url","content":"https://guocay.github.io/article/%E5%9F%BA%E4%BA%8E%E4%BA%8B%E4%BB%B6%E6%9C%BA%E5%88%B6%E7%9A%84%E6%9C%8D%E5%8A%A1%E5%86%85%E5%89%8A%E5%B3%B0%E5%92%8C%E4%BD%8E%E8%80%A6%E5%90%88-%E5%90%8C%E6%AD%A5-%E6%93%8D%E4%BD%9C%E7%9A%84%E6%80%9D%E8%80%83%E4%B8%8E%E5%AE%9E%E7%8E%B0.html"}],["meta",{"property":"og:site_name","content":"GuoCay"}],["meta",{"property":"og:title","content":"基于事件机制的服务内削峰和低耦合(同步)操作的思考与实现"}],["meta",{"property":"og:description","content":"问题描述 去年底在做项目的时候,我们有这样的一个需求,用户上传文档操作,首先将文档保存至服务器后再上传到ES数据库中解析文档.解析文档是一个很消耗时间的操作,我们不能等待解析完成后再对用户返回操作成功. 今年初在做项目的时候,我们有这样的一个需求,用户接收消息后,需要根据是否节假日且用户是否设置节假日自动回复对消息进行自动回复处理.且自动回复操作的成功与否要影响到消息是否接收.我们在分析需求后觉得这必须是一个事务性的服务.自动回复失败将不接收消息."}],["meta",{"property":"og:type","content":"article"}],["meta",{"property":"og:locale","content":"zh-CN"}],["meta",{"property":"article:author","content":"Guo Cay"}],["meta",{"property":"article:tag","content":"Mekatok"}],["meta",{"property":"article:tag","content":"开发笔记"}],["meta",{"property":"article:published_time","content":"2021-10-27T13:57:00.000Z"}],["script",{"type":"application/ld+json"},"{\\"@context\\":\\"https://schema.org\\",\\"@type\\":\\"Article\\",\\"headline\\":\\"基于事件机制的服务内削峰和低耦合(同步)操作的思考与实现\\",\\"image\\":[\\"\\"],\\"datePublished\\":\\"2021-10-27T13:57:00.000Z\\",\\"dateModified\\":null,\\"author\\":[{\\"@type\\":\\"Person\\",\\"name\\":\\"Guo Cay\\",\\"email\\":\\"guocay@gmail.com\\"}]}"]]},"headers":[{"level":2,"title":"问题描述","slug":"问题描述","link":"#问题描述","children":[]},{"level":2,"title":"问题思考","slug":"问题思考","link":"#问题思考","children":[]},{"level":2,"title":"解决方案","slug":"解决方案","link":"#解决方案","children":[]},{"level":2,"title":"PS","slug":"ps","link":"#ps","children":[]}],"git":{},"readingTime":{"minutes":5.42,"words":1625},"filePathRelative":"article/基于事件机制的服务内削峰和低耦合-同步-操作的思考与实现.md","localizedDate":"2021年10月27日","excerpt":"<h2> 问题描述</h2>\\n<blockquote>\\n<p>去年底在做项目的时候,我们有这样的一个需求,用户上传文档操作,首先将文档保存至服务器后再上传到ES数据库中解析文档.解析文档是一个很消耗时间的操作,我们不能等待解析完成后再对用户返回操作成功.</p>\\n</blockquote>\\n<blockquote>\\n<p>今年初在做项目的时候,我们有这样的一个需求,用户接收消息后,需要根据是否节假日且用户是否设置节假日自动回复对消息进行自动回复处理.且自动回复操作的成功与否要影响到消息是否接收.我们在分析需求后觉得这必须是一个事务性的服务.自动回复失败将不接收消息.</p>\\n</blockquote>","autoDesc":true}');export{t as data};
